for(i=0;i&lt;n-1;i++)
{
k = i;
for(j=i+1;j&lt;n;j++)
{
if(a[j] &lt; a[k])
k = j;
}
if (k != i)
{
t = a[i];
a[i] = a[k];
a[k] = t;
}
}
