<pre>#include
#include
#include 

using namespace std;
typedef int TelemType;
typedef struct BinaryTreeNode
{
	TelemType data;
	struct BinaryTreeNode *Left;
	struct BinaryTreeNode *Right;
}Node;

//创建	
Node* createBinaryTree()
{
	Node *p;
	TelemType ch;
	cin&gt;&gt;ch;
	if(ch==0)
	{
		p=NULL;
	} 
	else
	{
		p=(Node*)malloc(sizeof(Node));
		p-&gt;data=ch;
		p-&gt;Left=createBinaryTree();
		p-&gt;Right=createBinaryTree();
	}
	return p;
}


//二叉树结点总数目
int Nodenum(Node* root)
{
	if(root==NULL)
	{
		return 0;
	}
	else
	{
		return 1+Nodenum(root-&gt;Left)+Nodenum(root-&gt;Right);
	}
}



//二叉树子节点数
int Leafnum(Node* root)
{
	if(!root)
	{
		return 0;
	}
	else if((root-&gt;Left==NULL) &amp;&amp; (root-&gt;Right==NULL))
	{
		return 1;
	}
	else
	{
		return (Leafnum(root-&gt;Left)+Leafnum(root-&gt;Right));
	}
}

int DepthOfTree(Node* root)
{
	if(root)
	{
		return DepthOfTree(root-&gt;Left)&gt;DepthOfTree(root-&gt;Right)?DepthOfTree(root-&gt;Left)+1:DepthOfTree(root-&gt;Right)+1;
	}
	if(root == NULL)
	{
		return 0;
	}
}


void preOrderTraverse(Node* root)
{
	if(root)
	{
		cout&lt;data&lt;&lt;' '; preOrderTraverse(root-&gt;Left);
		preOrderTraverse(root-&gt;Right);
	}
}



void inOrderTraverse(Node* root)
{
	if(root)
	{
		inOrderTraverse(root-&gt;Left);
		cout&lt;data&lt;&lt;' '; inOrderTraverse(root-&gt;Right);
	}
}

void lastOrderTraverse(Node* root)
{
	if(root)
	{
		lastOrderTraverse(root-&gt;Left);
		lastOrderTraverse(root-&gt;Right);
		cout&lt;data&lt;&lt;' ';
	}
}


int main()
{
    Node *root = NULL;
    root = createBinaryTree();
    printf("二叉树建立成功");
    cout&lt;&lt;endl;

    cout&lt;&lt;"二叉树总节点数为："&lt;&lt;Nodenum(root)&lt;&lt;endl;

    cout&lt;&lt;"二叉树深度为："&lt;&lt;DepthOfTree(root)&lt;&lt;endl;

    cout&lt;&lt;"二叉树叶子节点数为："&lt;&lt;Leafnum(root)&lt;&lt;endl;

    cout&lt;&lt;"前序遍历结果:"&lt;&lt;endl;
    preOrderTraverse(root);
    cout&lt;&lt;endl;

    cout&lt;&lt;"中序遍历结果:"&lt;&lt;endl;
    inOrderTraverse(root);
    cout&lt;&lt;endl;

    cout&lt;&lt;"后序遍历结果:"&lt;&lt;endl;
    lastOrderTraverse(root);
    cout&lt;&lt;endl;

    return 0;
}</pre>
